import React, { useEffect, useState } from "react";
import { Redirect } from "react-router-dom";
import isOnline from "is-online";

import { IonAlert, IonPage } from "@ionic/react";

import "./splash.css";

const Splash = () => {
  const [showAlert, setShowAlert] = useState(false);
  const [myError, setMyError] = useState("");
  const [allOk, setAllOk] = useState(false);

  useEffect(() => {
    const checkOnline = async () => {
      try {
        let onlineCheck = await isOnline();
        setTimeout(() => {
          if (!onlineCheck) {
            setMyError("No Internet Connection");
            setShowAlert(true);
          } else {
            setAllOk(true);
          }
        }, 2000);
      } catch (err) {
        console.log(err);
      }
    };
    checkOnline();
  }, []);
  const imgsrc =
    "data:image/png;base64,";

  return (
    <IonPage>
      <div className="fullheight xc">
        <div>
          <img src={imgsrc} alt="xtreme-id" />
        </div>
      </div>
      <IonAlert
        isOpen={showAlert}
        onDidDismiss={() => setShowAlert(false)}
        cssClass="my-custom-class"
        header={"Error"}
        message={myError}
        buttons={["OK"]}
      />
      {allOk && <Redirect to="/organization" />}
    </IonPage>
  );
};

export default Splash;
